import React, { useEffect, useState } from 'react';
import './matrix.scss';


const Matrix = () => {

		const [pointer, setPointer] = useState({
				x:0,
				y:0
		})

		useEffect(() => {
				window.addEventListener('mousemove', mousePointer)
				function mousePointer(e) {
console.log(e);
						pointer.style = setPointer({
								x: e.clientX,
								y: e.clientY
						})
				}
		},[])


return(
		<div className='matrixContainer'>

		        <div className='displayClip'
				style={{'clipPath': `circle(50px at ${pointer.x}px ${pointer.y}px)`}}/>
				<div className='displayClip' 
				style={{'clipPath': `circle(50px at ${pointer.x}px ${pointer.y}px)`, 'transition':'all .01s linear', 'opacity':'.6'}}/>
				<div className='displayClip'
				style={{'clipPath': `circle(50px at ${pointer.x}px ${pointer.y}px)`, 'transition':'all .02s linear', 'opacity':'.4'}}/>
				<div className='displayClip'
				style={{'clipPath': `circle(50px at ${pointer.x}px ${pointer.y}px)`, 'transition':'all .03s linear', 'opacity':'.2'}}/>
				<div className='displayClip'
				style={{'clipPath': `circle(50px at ${pointer.x}px ${pointer.y}px)`, 'transition':'all .04s linear', 'opacity':'.1'}}/>
				<div className='displayClip'
				style={{'clipPath': `circle(50px at ${pointer.x}px ${pointer.y}px)`, 'transition':'all .5s linear', 'opacity':'.1'}}/> 

		</div>
)};

export default Matrix;
