import React, { useState, useEffect } from 'react';
import './cursorAnim.scss';

const CursorAnim = () => {

		const [cursor, setCursor] = useState({
				x: 0,
				y: 0
		})

		useEffect(() => {

				window.addEventListener('mousemove', mouseCursor);

				function mouseCursor(e) {
						cursor.style = setCursor({
								x: e.clientX,
								y: e.clientY
						})
				}

		},[cursor.x, cursor.y]);

return(

