const MainReducer = (main, action) => {

		switch(action.type){
				case 'musicSwitch':
						return{
						...main, 'music': !main.music 
						};
				case 'frenchSwitch':
						return{
						...main, 'french': !main.french
						};
				case 'pageHome':
						return{
						...main, 'page': 'home'
						};
				case 'pageAbout':
						return{
						...main, 'page': 'about'
						};
				case 'pageSkills':
						return{
						...main, 'page': 'skills'
						};
				case 'pageProjects':
						return{
						...main, 'page': 'projects'
						};
				case 'pageContact':
						return{
						...main, 'page': 'contact'
						};
				case 'navbarAlt':
						return{
						...main, 'navbar': 'alt'
						};
				case 'navbarReg':
						return{
						...main, 'navbar': 'reg'
						};
				default:
						return main;
		};
};

export default MainReducer;
