const mainReducer = (main, action) => {

		switch(action.type){
				case 'musicSwitch':
						return{
						...main, [music]: !music 
						};
				case 'themeSwitch':
						return{
						...main, [dark]: !dark 
						};
				default:
						return main;
		};
};

export default mainReducer;
