import React, { useContext, useEffect, useState } from 'react';
import './layoutPage.scss';
import Balise, { BaliseOn, BaliseOff } from '../balise/balise.js';
import { MainContext } from '../../context/mainContext.js';
import  { AboutDb, SkillsDb, ContactDb } from '../../database/database.js';


const DuoPage = (props) => {

		const { main } = useContext(MainContext);

		const [database, setDatabase] = useState(AboutDb);

		useEffect(() => {
						if(main.page === 'about') setDatabase(AboutDb)
						else if(main.page === 'skills') setDatabase(SkillsDb)
						else if(main.page === 'contact') setDatabase(ContactDb)
		}, [main])

		//-----      Contact Page      -----//

		function handleChange(e) {
				console.log(e.target, e.target.name, e.target.value);
		}

		const contactForm = (
				<div className='leftBlock'>
						<BaliseOn content='form'/>
						<form>
								<input className='formInput' name='name' defaultValue='name' type='text' onChange={handleChange} required/>
								<input className='formInput' name='email' defaultValue='email' type='email' onChange={handleChange} required/>
								<input className='formInput' name='subject' defaultValue='subject' type='text' onChange={handleChange} required/>
								<input className='formInput' name='message' defaultValue='message...' type='textarea' onChange={handleChange} required/>
						</form>
						<BaliseOff content='form'/>
				</div>
		)  

return(
		<div className='duoPage'>
				<div className='leftSide'>
						<Balise/>
						<div className='leftBlock'>
								<BaliseOn content='h1'/>
								<h1>{database.title}</h1>	
								<BaliseOff content='h1'/>
						</div>
						<div className='leftBlock'>
								<BaliseOn content='p'/>
								<p>{database.content}</p>
								<BaliseOff content='p'/>
						</div>
						{main.page === 'contact' && contactForm}
				</div>
				<div className='rightSide'>
						rightside
				</div>
		</div>
)};

export default DuoPage;
